# -*- coding: utf-8 -*-

import struct
import base64

n = 10240099
b1 = (n & 0xff000000) >> 24
b2 = (n & 0xff0000) >> 16
b3 = (n & 0xff00) >> 8
b4 = n & 0xff
bs = bytes([b1, b2, b3, b4])
print(bs)

print(struct.pack('>I', 10240099))
print(struct.unpack('>IH', b'\xf0\xf0\xf0\xf0\x80\x80'))

with open('2019_8_14.bmp','rb') as file:
    pic_info = file.read(30)
    print(pic_info)
    print(struct.unpack('<ccIIIIIIHH',pic_info))
    
bmp_data = base64.b64decode('')

def bmp_info(data):
    tempdata = data[0:30]
    
    unpack_data = struct.unpack('<ccIIIIIIHH',tempdata)
    assert (b'B', b'M') == (unpack_data[0], unpack_data[1])
    return{
            'width':unpack_data[6],
            'height':unpack_data[7],
            'color':unpack_data[9]
            }

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')